<div fxLayout="column" class="blue">
	<h1 *ngIf="visibleGreeting" class="yellow">{{ title }}</h1>
	<h1 *ngIf="visibleResultsH1" class="yellow">{{ resultTitle }}</h1>
	<div class="loan-form" fxFlex>
		<div
			*ngIf="
				(form['loanAmount'].errors && form['loanAmount'].touched) ||
				(form['numberOfYears'].errors && form['numberOfYears'].touched) ||
				(form['interest'].errors && form['interest'].touched)
			"
		>
			<div class="msg-error">Must be a numeric value.</div>
		</div>
		<mat-card class="mat-elevation-z8">
			<mat-card-content class="light-blue">
				<div *ngIf="visibleResults">
					<h1>
						Your monthly payment will be: <br />
						<span class="result-nums">{{ monthlyPayment | currency }}</span>
						<br /><br />Interest paid over loan term: <br />
						<span class="result-nums">{{ totalInterest | currency }}</span>
					</h1>
				</div>
				<form
					[formGroup]="loanForm"
					(ngSubmit)="onSubmit($event); loanForm.reset()"
					fxLayout="column"
					fxLayoutGap="16px"
					*ngIf="visibleForm"
				>
					<mat-form-field fxFlex appearance="fill">
						<mat-label>Loan Amount</mat-label>
						<input
							type="text"
							name="txtLoanAmount"
							required
							id="txtLoanAmount"
							matInput
							formControlName="loanAmount"
						/>
					</mat-form-field>
					<mat-form-field fxFlex appearance="fill">
						<mat-label>Interest</mat-label>
						<input
							type="text"
							name="txtInterest"
							required
							id="txtInterest"
							matInput
							formControlName="interest"
						/>
					</mat-form-field>
					<mat-form-field fxFlex appearance="fill">
						<mat-label>Number of Years</mat-label>
						<input
							type="text"
							name="txtNumOfYears"
							required
							id="txtNumOfYears"
							matInput
							formControlName="numberOfYears"
						/>
					</mat-form-field>
					<button
						type="submit"
						mat-raised-button
						[disabled]="!loanForm.valid"
						color="accent"
						(click)="calculateLoan()"
					>
						Calculate
					</button>
				</form>
			</mat-card-content>
		</mat-card>
	</div>
</div>
